import React, { useState, useEffect } from "react";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
function Details(props) {
  const [valueSub, setValueSub] = useState("");
  let { value } = props;

  useEffect(() => {
    let data = ShowDataFind(
      new Date(value).getFullYear(),
      new Date(value).getMonth(),
      new Date(value).getDate()
    );
    console.log(data);
  });
  function ShowDataFind(year, month, day) {
    let date = new Date(year, month, day).getTime();
    let result = {
      name: "",
      data: [],
    };
    let resultSchedule = JSON.parse(localStorage.getItem("result"));
    if (resultSchedule.user == "") {
    } else {
      resultSchedule.data.map((subject) => {
        let name = subject.name;
        subject.time.map((item) => {
          if (date == item.time) {
            let subj = {
              name: name,
              details: item,
            };
            result.data.push(subj);
          }
        });
      });
    }
    return result;
  }
  return (
    <div>
      <h2>
        {" "}
        Lịch học ngày {JSON.stringify(new Date(value).getDate())}/
        {JSON.stringify(new Date(value).getMonth() + 1)}/
        {JSON.stringify(new Date(value).getFullYear())}
      </h2>
      <p></p>
    </div>
  );
}
export default Details;
