import React, { useState } from "react";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
function Details(props) {
  let { value } = props;
  async function ShowDataFind(year, month, day) {
    let date = new Date(year, month - 1, day).getTime();
    let result = {
      name: "",
      data: [],
    };
    await this.getData();
    if (this.name == "") {
    } else {
      result.name = this.name;
      this.schedule.map((subject) => {
        let name = subject.name;

        subject.time.map((item) => {
          if (date == item.time) {
            let subj = {
              name: name,
              details: item,
            };
            result.data.push(subj);
          }
        });
      });
    }
    return result;
  }
  return (
    <div>
      <h2>
        {" "}
        Lịch học ngày {JSON.stringify(new Date(value).getDate())}/
        {JSON.stringify(new Date(value).getMonth() + 1)}/
        {JSON.stringify(new Date(value).getFullYear())}
      </h2>
      <p></p>
    </div>
  );
}
export default Details;
