import React, { useState, useEffect } from "react";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import Details from "./Component/Details";
function App() {
  const [value, onChange] = useState(new Date());
  const [date, setDatess] = useState(
    new Date(
      new Date().getFullYear(),
      new Date().getMonth(),
      new Date().getDay()
    ).getTime()
  );

  let onClickDay = (value) => {
    let day = value.getDay();
    let month = value.getMonth();
    let year = value.getFullYear();
    let dataDate = new Date(year, month, day).getTime();
    setDatess(dataDate);
  };
  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <Calendar
        onChange={onChange}
        value={value}
        onClickDay={() => {
          onClickDay(value);
        }}
      />
      <Details value={date}></Details>
    </div>
  );
}
export default App;
